const inputBox = document.querySelector('.input-box')
const searchBtn = document.getElementById('searchBtn')
const weather_img = document.querySelector('.weather-img')
const temperature = document.querySelector('.temperature')
const description = document.querySelector('.description')
const humidity = document.getElementById('humidity')
const wind_speed = document.getElementById('wind-speed')


async function checkWeather(city) {
    const api_key = "3b588789dfaba8d7172ced7c7180a1c2"
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${api_key}`

    const weather_data =await fetch(`${url}`).then(response => response.json())
    
    if (weather_data.cod === `404`) {
        console.log("error");
        return;
        
    }
    temperature.innerHTML = `${Math.round(weather_data.main.temp - 273.15)}Â°C`

    description.innerHTML = `${weather_data.weather[0].description}`

    humidity.innerHTML = `${weather_data.main.humidity}%`
    wind_speed.innerHTML = `${weather_data.wind.speed}Km/H`

    switch (weather_data.weather[0].main) {
        case 'Clouds':
            weather_img.src = "data:image/png;base64,"
           break;

        case 'Clear':
            weather_img.src = "data:image/png;base64,"
        break;

        case 'Rain':
            weather_img.src = "data:image/jpeg;base64,"
        break;

        case 'Mist':
            weather_img.src = "data:image/png;base64,"
        break;

        case 'Snow':
            weather_img.src = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRjLHaBk1x6cR8J8A6EPgc8_OksPYopGwC62A&s"
        break;
        } 
   
    
}

searchBtn.addEventListener("click", ()=>{
    checkWeather(inputBox.value);
});